from web3 import Web3
import json

from config import private_key


eth_rpc = 'https://rpc.ankr.com/eth'
arb_rpc = 'https://rpc.ankr.com/arbitrum'


# подключение
w3 = Web3(Web3.HTTPProvider(endpoint_uri=arb_rpc))
print(f'Is connected: {w3.is_connected()}')

# получение общих параметров из блокчейна
print(f'current block number: {w3.eth.block_number}')
print(w3.eth.get_block(w3.eth.block_number))
print(f'gas price: {w3.eth.gas_price / 10 ** 9} GWEI')
print(f'number of current chain is {w3.eth.chain_id}')


# получение параметров кошелька из блокчейна
account = w3.eth.account.from_key(private_key=private_key)
print(f'address: {account.address}')
# print(f'private_key: {account.key.hex()}')
print(f'transaction count: {w3.eth.get_transaction_count(account.address)}')


# проверка баланса
balance = w3.eth.get_balance(account.address)
print(f'balance of {account.address} = {balance / 10 ** 18}')


# инициализирование контракта
